create table product
(
    dtype       varchar(31)  not null,
    id          bigint       not null
        constraint product_pkey
            primary key,
    cost        numeric(19, 2),
    description bytea        not null,
    name        varchar(255) not null,
    price       numeric(19, 2),
    stock_id    bigint
        constraint fk90w0j7y2y7chsmk4v4k02ekqf
            references stock,
    type        varchar(255),
    discount    numeric(19, 2) default 0.00
);

alter table product
    owner to snackxpress;

